import { useState, useEffect } from "react";
import { TextField } from "@mui/material";
import { Link, useNavigate } from "react-router-dom";

const Desktop11 = ({ formData, setFormData }) => {
  const [numberOfDays, setNumberOfDays] = useState(1);
  const [startDate, setStartDate] = useState(
    new Date().toISOString().substr(0, 10)
  );
  const [endDate, setEndDate] = useState("");

  const handleNumberOfDaysChange = (event) => {
    setNumberOfDays(event.target.value);
  };
  console.log(formData);

  const calculateEndDate = (numberOfDays, startDate) => {
    if (!numberOfDays || !startDate) {
      setEndDate(startDate.getDate + 1);
      return;
    }
    const startDateObject = new Date(startDate);
    const endDateObject = new Date(startDateObject);
    endDateObject.setDate(
      startDateObject.getDate() + parseInt(numberOfDays, 10)
    );
    setFormData({
      ...formData,
      endDate: endDateObject.toISOString().substring(0, 10),
    });
  };
  const handleSubmit = (e) => {
    fetch("http://localhost:3001/posts", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => console.log("user info", data))
      .catch((error) => console.error("Error fetching user data:", error));
  };
  useEffect(() => {
    calculateEndDate(formData.noOfDays, formData.startDate);
  }, [formData.noOfDays, formData.startDate]);

  return (
    <div className="desktop-9-resp">
      <nav className="nav-container" id="navContainer">
        <div className="nav-items relative">
          <div className="absolute left-[8rem]">
            <Link className="circle [text-decoration:none]" to="/">
              <div className="text-black ml-[-1.7rem] mt-3">1</div>
              <div className="mt-5 text-xl w-32 text-black ml-[-1rem]">
                Customer Info
              </div>
            </Link>
            <Link
              className="cursor-pointer [text-decoration:none] absolute top-[0px] left-[752px] w-[82px] h-[83px] text-tomato"
              to="/license-details"
            >
              <div className="red circle">
                <div className="text-black ml-[-1.7rem] mt-3">3</div>
                <div className="mt-5 text-xl w-32 text-black ml-[-1rem]">
                  License Details
                </div>
              </div>
            </Link>

            <Link
              className="cursor-pointer [text-decoration:none] absolute top-[0px] left-[384px] w-[82px] h-[83px] text-tomato"
              to="/license-type"
            >
              <div className="circle">
                <div className="text-black ml-[-1.7rem] mt-3">2</div>
                <div className="mt-5 text-xl w-32 text-black ml-[-1rem]">
                  License Type
                </div>
              </div>
            </Link>
            <Link
              className="cursor-pointer [text-decoration:none] absolute top-[0px] left-[1119px] w-[82px] h-[83px] text-tomato"
              to="/preview"
            >
              <div className="circle">
                <div className="text-black ml-[-1.7rem] mt-3">4</div>
                <div className="mt-5 text-xl w-32 text-black ml-[-1rem]">
                  Preview
                </div>
              </div>
            </Link>
            <img
              className="absolute top-[40.5px] left-[138px] w-[220px] h-[3px]"
              alt=""
              src="/line-11.svg"
            />
            <img
              className="absolute top-[40.5px] left-[499px] w-[220px] h-[3px]"
              alt=""
              src="/line-11.svg"
            />
            <img
              className="absolute top-[40.5px] left-[866px] w-[220px] h-[3px]"
              alt=""
              src="/line-11.svg"
            />
          </div>
        </div>
      </nav>

      <div className="z-50bg-pink-300 w-[70%] m-auto grid gap-10 mt-10">
        <div className="grid gap-3">
          <div className="text-21xl font-bold text-black">Select Modules:</div>
          <div className="text-5xl font-medium">Modules to be enabled:</div>
          <Link
            className="cursor-pointer [text-decoration:none] w-[341px] h-[62px] text-center text-white"
            to="/module-selector"
          >
            <div className="relative rounded-xl bg-dimgray-200 w-[341px] h-[62px]">
              <div className="absolute top-3 left-14 text-5xl">
                Click here to select
              </div>
            </div>
          </Link>
        </div>

        <div className="grid gap-3">
          <div className="text-21xl font-bold">License Duration</div>
          <div className="text-6xl">
            Indicate the length of the License period in terms of days:
          </div>

          <TextField
            className=""
            sx={{
              width: 274,
              color: "black",
              "& .MuiInputBase-input.Mui-disabled": {
                WebkitTextFillColor: "#000000",
              },
            }}
            color="secondary"
            variant="outlined"
            type="number"
            id="dayinput"
            disabled={
              formData.licenseType === "Demo" ||
              formData.licenseType === "Trial"
            }
            placeholder="Enter no of days"
            size="medium"
            margin="none"
            required
            onBlur={(e) => {
              if (!formData.noOfDays) {
                setFormData({ ...formData, noOfDays: 1 });
              }
            }}
            value={formData.noOfDays}
            onChange={(e) => {
              setFormData({ ...formData, noOfDays: e.target.value });
            }}
          />
        </div>

        <div className="font-medium text-6xl mt-10">
          When do you want to get started
        </div>
        <div className="flex gap-5">
          <div className="grid gap-2">
            <div className="text-5xl">Start date</div>

            <TextField
              className="[border:none] bg-[transparent]"
              sx={{
                width: 300,
                "& .MuiInputBase-input.Mui-disabled": {
                  WebkitTextFillColor: "#000000",
                },
              }}
              color="secondary"
              variant="outlined"
              type="date"
              id="dateInput"
              placeholder="Default - Day of Application"
              size="medium"
              margin="none"
              disabled={
                formData.licenseType === "Demo" ||
                formData.licenseType === "Trial"
              }
              onChange={(e) => {
                setFormData({ ...formData, startDate: e.target.value });
              }}
              value={formData.startDate}
            />
          </div>

          <div className="grid gap-2">
            <div className="text-5xl">End Date</div>
            <TextField
              className="[border:none] bg-[transparent]"
              sx={{
                width: 300,
                "& .MuiInputBase-input.Mui-disabled": {
                  WebkitTextFillColor: "#000000",
                },
              }}
              color="secondary"
              variant="outlined"
              id="dateInput"
              disabled
              size="medium"
              margin="none"
              value={formData.endDate}
            />
          </div>
        </div>

        <div className="grid gap-3">
          <div>License Restrictions</div>
          <TextField
            className=""
            sx={{ width: 913 }}
            color="secondary"
            variant="outlined"
            multiline
            value={formData.licenseRestrictions}
            onChange={(e) => {
              setFormData({ ...formData, licenseRestrictions: e.target.value });
            }}
            id="licenserestrictioninput"
            placeholder="Add in some comments here (IF APPLICABLE)"
            margin="none"
          />
        </div>

        <div className="gap-3">
          <div>Additional Comments</div>
          <TextField
            className=""
            sx={{ width: 913 }}
            color="secondary"
            value={formData.comments}
            onChange={(e) => {
              setFormData({ ...formData, comments: e.target.value });
            }}
            variant="outlined"
            multiline
            id="additionalCommentsInput"
            placeholder="Add in some comments here (IF APPLICABLE)"
            margin="none"
          />
        </div>

        <Link
          to="/preview"
          onClick={handleSubmit}
          className="[text-decoration:none] cursor-pointer [border:none] p-0 bg-tomato rounded-sm w-[341px] h-[62px] flex flex-col items-center justify-center"
        >
          <div className="relative text-6xl font-inter text-white text-center flex items-center justify-center w-[278.35px] h-[50.47px] shrink-0">{`Next Step  ->`}</div>
        </Link>
      </div>

      <div className="absolute top-[2233px] left-[calc(50%_-_485px)] w-[970px] h-[655px] overflow-y-auto text-left font-inter">
        <div className="absolute top-[879px] left-[7px] w-[913px] h-[121px]">
          <p
            className="m-0 absolute top-[0px] left-[0px] font-bold inline-block w-[795px] h-[71px]"
            id="Licenserestrictionsheading"
          >
            License Restrictions
          </p>
        </div>
        <div className="absolute top-[1085px] left-[0px] w-[913px] h-[113px]">
          <p
            className="m-0 absolute top-[0px] left-[0px] font-bold inline-block w-[888px] h-[71px]"
            id="additionalCommentsHeading"
          >
            Additional Comments:
          </p>
        </div>

        <div className="absolute top-[272px] left-[17px] w-[785px] h-[522px] text-6xl">
          <p
            className="m-0 absolute top-[71px] left-[0px] font-medium text-dimgray-100 inline-block w-[695px]"
            id="licenseDurationSubheading"
          >{`Indicate the length of the license period in terms of days: `}</p>
          <p
            className="m-0 absolute top-[338px] left-[0px] font-medium text-dimgray-100 inline-block w-[491px]"
            id="datesubheading"
          >
            When do you want to get started?
          </p>
          <p
            className="m-0 absolute top-[0px] left-[0px] text-21xl font-bold inline-block w-[346px] h-[71px]"
            id="licenseDurationHeading"
          >
            License Duration:
          </p>
          <div className="absolute top-[417px] left-[0px] font-hp-simplified-jpan inline-block w-[131px]">
            Start Date:
          </div>
          <div className="absolute top-[410px] left-[515px] font-hp-simplified-jpan inline-block w-[131px]">
            End Date:
          </div>
          <p
            className="m-0 absolute top-[147px] left-[0px] font-assistant inline-block w-[227px]"
            id="dayinput"
          >
            Enter the No of days:
          </p>
          <TextField
            className="[border:none] bg-[transparent] absolute top-[210px] left-[0px]"
            sx={{
              width: 274,
              color: "black",
              "& .MuiInputBase-input.Mui-disabled": {
                WebkitTextFillColor: "#000000",
              },
            }}
            color="secondary"
            variant="outlined"
            type="number"
            id="dayinput"
            disabled={
              formData.licenseType === "Demo" ||
              formData.licenseType === "Trial"
            }
            placeholder="Enter no of days"
            size="medium"
            margin="none"
            required
            onBlur={(e) => {
              if (!formData.noOfDays) {
                setFormData({ ...formData, noOfDays: 1 });
              }
            }}
            value={formData.noOfDays}
            onChange={(e) => {
              setFormData({ ...formData, noOfDays: e.target.value });
            }}
          />
          {/* <div
            className="absolute top-[459px] left-[511px] rounded-mini bg-whitesmoke-100 box-border w-[274px] h-[63px] border-[3px] border-solid border-gainsboro-100"
            id="calculatedEndDate"
          >
          </div> */}
        </div>
      </div>
    </div>
  );
};

export default Desktop11;
