import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Desktop9 from "./Desktop9";
import Desktop10 from "./Desktop10";
import Desktop11 from "./Desktop11";
import Desktop12 from "./Desktop12";
import Desktop14 from "./Desktop14";
import Desktop15 from "./Desktop15";

import { useEffect } from "react";

function App() {
  const action = useNavigationType();
  const location = useLocation();
  const pathname = location.pathname;

  useEffect(() => {
    if (action !== "POP") {
      window.scrollTo(0, 0);
    }
  }, [action, pathname]);

  useEffect(() => {
    let title = "";
    let metaDescription = "";

    if (title) {
      document.title = title;
    }

    if (metaDescription) {
      const metaDescriptionTag = document.querySelector(
        'head > meta[name="description"]'
      );
      if (metaDescriptionTag) {
        metaDescriptionTag.content = metaDescription;
      }
    }
  }, [pathname]);

  return (
    <Router>
      <Switch>
        <Route path="/" exact component={Desktop9} />
        <Route path="/license-type" component={Desktop10} />
        <Route path="/preview" component={Desktop12} />
        <Route path="/selected-modules" component={Desktop15} />
        <Route path="/module-selector" component={Desktop14} />
        <Route path="/license-details" component={Desktop11} />
      </Switch>
    </Router>
  );
}
export default App;
